import { apiConfig } from "../config";

export const getPostsApi = ({
  numberOfPosts,
  groupsIds,
  timestamps,
}: {
  numberOfPosts: number;
  groupsIds: Array<number>;
  timestamps: Array<number>;
}) =>
  apiConfig
    .get(
      `posts?groupsIds=${groupsIds}&numberOfPosts=${numberOfPosts}&timestamps=${timestamps}`
    )
    .json();
